arguments.argument:
distance = point_distance(x, y, obj_player.x, obj_player.y);
if (distance > 400 || distance < 100) {
    current_speed -= deceleration;
    if (current_speed < 0) {
        current_speed = 0;
        image_speed = 0;
        image_index = 0;
    }
} else {
    image_speed = 0.1;
    current_speed += acceleration;
    if (current_speed > max_speed) {
        current_speed = max_speed;
    }
}
if (distance < 50 && loaded) {
    loaded = false;
    alarm[7] = reload_time;
    script_execute(scr_hurt_player, dammage);
}
mp_potential_step(obj_player.x, obj_player.y, current_speed, false);

script_execute(scr_fix_depth);

if (x - xprevious < 0) {
    image_xscale = 1;
} else if (x - xprevious > 0) {
    image_xscale = -1;
}

script_execute(scr_unblock);
